use rocket::config::{Config, Environment};
use std::env;

let mut port = 8000;
match env::var("OPTIC_API_PORT") {
    Ok(val) => port = val.parse::<u16>().unwrap(),
    Err(e) => {}
}

let config = Config::build(Environment::Staging)
    .port(port)
    .unwrap();

rocket::custom(config)
    .mount("/", routes![hello])
    .launch();
